mapper.select:
SELECT ID
      ,CONTENTS
      ,STATUS_TYPE
      ,REG_DTS
      ,MOD_DTS
  FROM TODO
 WHERE ID = #{id}

AND CONTENTS LIKE #{contents}||'%'


AND STATUS_TYPE = #{statusType}


AND MOD_DTS between #{modDtsAfter} and #{modDtsBefore}


AND REG_DTS between #{regDtsAfter} and #{regDtsBefore}

SELECT ID
      ,CONTENTS
      ,STATUS_TYPE
      ,REG_DTS
      ,MOD_DTS
  FROM TODO
 WHERE 1=1

  AND ID IN

    #{item}



  AND CONTENTS LIKE #{contents}||'%'


  AND STATUS_TYPE = #{statusType}


  AND MOD_DTS between #{modDtsAfter} and #{modDtsBefore}


  AND REG_DTS between #{regDtsAfter} and #{regDtsBefore}

mapper.update:
UPDATE TODO
SET CONTENTS = #{contents}

   ,STATUS_TYPE = #{statusType}

WHERE ID = #{id}
